/*! DataTables Tailwind CSS integration
 */

/*
 * This is a tech preview of Tailwind CSS integration with DataTables.
 */

// Set the defaults for DataTables initialisation
$.extend( true, DataTable.defaults, {
	dom:
		"<'grid grid-cols-1 md:grid-cols-2'" +
			"<'self-center'l>" +
			"<'self-center place-self-end'f>" +
			"<'my-2 col-span-2 overflow-x-auto'tr>" +
			"<'self-center'i>" +
			"<'self-center place-self-end'p>" +
		">",
	renderer: 'tailwindcss'
} );


// Default class modification
$.extend( DataTable.ext.classes, {
	sWrapper:      "dataTables_wrapper dt-tailwindcss",
	sFilterInput:  "form-input border-slate-300 focus:outline-none focus:border-custom-500 inline-block w-auto ml-2",
	sLengthSelect: "border px-3 py-2 rounded-lg border-slate-200 dark:bg-slate-800 dark:border-slate-600 dark:focus:border-custom-500",
	sProcessing:   "dt-processing uk-panel",
	tailwindcss: {
		paging: {
			active: 'font-semibold bg-slate-100 dark:bg-slate-700/75',
			notActive: 'bg-white dark:bg-slate-800',
			button: 'relative inline-flex justify-center items-center space-x-2 border px-4 py-2 -mr-px leading-6 hover:z-10 focus:z-10 active:z-10 border-slate-200 active:border-slate-200 active:shadow-none dark:border-slate-700 dark:active:border-slate-700',
			first: 'rounded-l-lg',
			last: 'rounded-r-lg',
			enabled: 'text-slate-800 hover:text-slate-900 hover:border-slate-300 hover:shadow-sm focus:ring focus:ring-slate-300 focus:ring-opacity-25 dark:text-slate-300 dark:hover:border-slate-600 dark:hover:text-slate-200 dark:focus:ring-slate-600 dark:focus:ring-opacity-40',
			notEnabled: 'text-slate-300 dark:text-slate-600'
		},
		table: 'dataTable min-w-full text-sm align-middle whitespace-nowrap',
		thead: {
			row: 'border-b border-slate-100 dark:border-slate-700/50',
			cell: 'px-3 py-4 text-slate-900 bg-slate-200/50 font-semibold text-left dark:text-slate-50 dark:bg-slate-700/25 group-[.bordered]:border group-[.bordered]:border-slate-200'
		},
		tbody: {
			row: 'group-[.stripe]:even:bg-slate-50 group-[.stripe]:dark:even:bg-slate-900/50 transition-all duration-150 ease-linear group-[.hover]:hover:bg-slate-50 [&.selected]:bg-custom-500  [&.selected]:text-custom-50',
			cell: 'p-3 group-[.bordered]:border group-[.bordered]:border-slate-200'
		},
		tfoot: {
			row: 'group-[.stripe]:even:bg-slate-50 group-[.stripe]:dark:even:bg-slate-900/50 transition-all duration-150 ease-linear group-[.hover]:hover:bg-slate-50',
			cell: 'p-3 text-left group-[.bordered]:border group-[.bordered]:border-slate-200'
		},
	}
} );

// Abstract out DataTable's awful class property names were we can,
// others are handled below
DataTable.ext.classes.sTable = DataTable.ext.classes.tailwindcss.table;
DataTable.ext.classes.sStripeOdd = DataTable.ext.classes.tailwindcss.tbody.row;
DataTable.ext.classes.sStripeEven = DataTable.ext.classes.tailwindcss.tbody.row;
DataTable.ext.classes.sHeaderTH = DataTable.ext.classes.tailwindcss.thead.cell;
DataTable.ext.classes.sFooterTH = DataTable.ext.classes.tailwindcss.tfoot.cell;
DataTable.defaults.column.sClass = DataTable.ext.classes.tailwindcss.tbody.cell;

// Eventually the classes and styles to apply from above will be merged into
// DataTables core, but for now we add them in a Tailwind CSS specific file
// since this is the only one that uses them this way at the moment.
$(document).on('init.dt', function (e, settings) {
	let thead = settings.nTHead;
	let classes = DataTable.ext.classes.tailwindcss;

	$(thead).addClass(classes.thead.row);
});


/* UIkit paging button renderer */
DataTable.ext.renderer.pageButton.tailwindcss = function ( settings, host, idx, buttons, page, pages ) {
	var api     = new DataTable.Api( settings );
	var classes = settings.oClasses.tailwindcss.paging;
	var lang    = settings.oLanguage.oPaginate;
	var aria = settings.oLanguage.oAria.paginate || {};
	var btnDisplay;

	var attach = function( container, buttons ) {
		var i, ien, node, button;
		var clickHandler = function ( e ) {
			e.preventDefault();
			if ( !$(e.currentTarget).hasClass('disabled') && api.page() != e.data.action ) {
				api.page( e.data.action ).draw( 'page' );
			}
		};

		for ( i=0, ien=buttons.length ; i<ien ; i++ ) {
			button = buttons[i];

			if ( Array.isArray( button ) ) {
				attach( container, button );
			}
			else {
				let enabled = false;
				let active = false;

				btnDisplay = '';

				switch ( button ) {
					case 'ellipsis':
						btnDisplay = '&#x2026;';
						break;

					case 'first':
						btnDisplay = lang.sFirst;

						if (page > 0) {
							enabled = true;
						}
						break;

					case 'previous':
						btnDisplay = lang.sPrevious;

						if (page > 0) {
							enabled = true;
						}
						break;

					case 'next':
						btnDisplay = lang.sNext;

						if (page < pages-1) {
							enabled = true;
						}
						break;

					case 'last':
						btnDisplay = lang.sLast;

						if (page < pages-1) {
							enabled = true;
						}
						break;

					default:
						btnDisplay = button + 1;
						enabled = true;

						if (page === button) {
							active = true;
						}
						break;
				}

				if ( btnDisplay ) {
					var className = classes.button + ' ' +
						(active ? classes.active : classes.notActive) + ' ' +
						(enabled ? classes.enabled : classes.notEnabled);

					node = $('<a>', {
							'href': enabled ? '#' : null,
							'aria-controls': settings.sTableId,
							'aria-disabled': enabled ? null : 'true',
							'aria-label': aria[ button ],
							'role': 'link',
							'aria-current': active ? 'page' : null,
							'data-dt-idx': button,
							'tabindex': enabled ? settings.iTabIndex : -1,
							'class': className,
							'id': idx === 0 && typeof button === 'string' ?
								settings.sTableId +'_'+ button :
								null
						} )
						.html( btnDisplay )
						.appendTo( container );

					settings.oApi._fnBindAction(
						node, {action: button}, clickHandler
					);
				}
			}
		}
	};

	var hostEl = $(host);
	var activeEl = hostEl.find(document.activeElement).data('dt-idx');
	var paginationEl = hostEl.children('ul.pagination');

	if (paginationEl.length) {
		paginationEl.empty();
	}
	else {
		paginationEl = hostEl
			.html('<div class="text-center dark:text-slate-100"/>')
			.children('div');
	}

	attach(
		paginationEl,
		buttons
	);

	paginationEl.children(':first-child').addClass(classes.first);
	paginationEl.children(':last-child').addClass(classes.last);

	if ( activeEl !== undefined ) {
		hostEl.find('[data-dt-idx='+activeEl+']').trigger('focus');
	}
};

